export const fetchMovies2 = async (URL, page) => {
  const response =  await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate&page=${page}&with_genres=${URL}`);
  if (response.ok) {
      const data = response.json();

  }
}
