import React, {useEffect, useState} from 'react';
import styled from './Header.module.css';
import {useHistory} from "react-router-dom";

export const Header = () => {
    const [value, setValue] = useState(0);
    const history = useHistory();

    useEffect( () => {
        if (value === 0) {
            history.push("/");
        } else if (value === 1) {
            history.push("/movies");
        } else if (value === 2) {
            history.push("/series");
        } else if (value === 3) {
            history.push("/search");
        }
    }, [value, history]);
    return (
        <div>
            <span
                className={styled.header}
            >Movies App</span>
        </div>
    );
};
