import React from 'react';
import {createMuiTheme} from "@material-ui/core";
import {Pagination, ThemeProvider} from "@mui/material";

const darkTheme = createMuiTheme({
    palette: {
        type: "dark",
    },
});

export const CustomPagination = ({setPage, numOfPages = 10}) => {const handlePageChange = (page) => {
    setPage(page);
    window.scroll(0, 0);
};

    return (
        <div
            style={{
                width: "100%",
                display: "flex",
                justifyContent: "center",
                marginTop: 10,
            }}
        >
            <ThemeProvider theme={darkTheme}>
                <Pagination
                    onChange={(e) => handlePageChange(e.target.textContent)}
                    count={numOfPages}
                    color="primary"
                    hideNextButton
                    hidePrevButton
                />
            </ThemeProvider>
        </div>
    );
};
